import React, { useState } from "react";
import CSVImporter from "./CSVImporter";

// … (parseXMLToObject, fetchProductOrCombination, calcMarge, updateStock inchangés) …

export default function CommandeView() {
  const [produits, setProduits] = useState([]);

  const enrichWithAPI = async (rows) => {
    // … (détection colonnes + fetch API) …
    setProduits(data);
  };

  // Nouvelle handler pour modifier la quantite en local
  const onChangeQuantite = (index, newQty) => {
    setProduits(prev =>
      prev.map((p, i) =>
        i === index
          ? { ...p, quantite: newQty }
          : p
      )
    );
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Commande Produits</h1>
      <CSVImporter onDataLoaded={enrichWithAPI} />
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-300 rounded-lg text-sm">
          <thead className="bg-gray-100">
            <tr>
              {/* … autres en-têtes … */}
              <th className="px-4 py-2">Qté CSV</th>
              {/* … */}
            </tr>
          </thead>
          <tbody>
            {produits.map((p, i) => (
              <tr key={i} className="border-t">
                {/* … autres cellules … */}
                <td className="px-4 py-2">
                  <input
                    type="number"
                    min="0"
                    className="w-16 border rounded px-1"
                    value={p.quantite}
                    onChange={e => onChangeQuantite(i, parseInt(e.target.value, 10) || 0)}
                  />
                </td>
                <td className="px-4 py-2">{p.stock}</td>
                {/* … */}
                <td className="px-4 py-2">
                  <button
                    className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700"
                    onClick={() =>
                      updateStock(p.prestaId, p.quantite, p.stock, p.isCombination)
                    }
                  >
                    MAJ Stock
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
