// Code fourni par l'utilisateur

import React, { useState } from "react";
import CSVImporter from "./CSVImporter";

// Assure-toi d'avoir parseXMLToObject défini plus haut, par exemple :
function parseXMLToObject(xmlString, type = 'product') {
  const parser = new DOMParser();
  const xml = parser.parseFromString(xmlString, 'application/xml');
  const item = xml.querySelector(type);
  if (!item) return null;
  const getText = tag => item.querySelector(tag)?.textContent.trim() || '';
  return {
    id:       getText('id'),
    // Ces deux champs sont sous <wholesale_price> et <price> côté product, ou pas exposés côté combination
    wholesale_price: parseFloat(getText('wholesale_price') || '0'),
    price:    parseFloat(getText('price') || '0'),
    quantity: parseInt(getText('quantity') || '0', 10)
  };
}

async function fetchProductOrCombination(reference) {
  // helper pour parser un <product> ou <combination>
  function parseXMLToObject(xmlString, tag) {
    const parser = new DOMParser();
    const doc    = parser.parseFromString(xmlString, 'application/xml');
    const el     = doc.querySelector(tag);
    if (!el) return null;
    const txt    = t => el.querySelector(t)?.textContent.trim() || '';
    return {
      id:              txt('id'),
      wholesale_price: parseFloat(txt('wholesale_price') || '0'),
      price:           parseFloat(txt('price')           || '0'),
      quantity:        parseInt(  txt('quantity')        || '0', 10)
    };
  }

  // 1⃣ Tenter le produit simple
  let xml = await fetch(`http://localhost:4000/api/product/${encodeURIComponent(reference)}`)
                  .then(r => r.text())
                  .catch(() => null);
  let parsed = parseXMLToObject(xml, 'product');
  if (parsed && parsed.id) {
    return { 
      prestaId: parsed.id,
      isCombination: false,
      purchasePrice: parsed.wholesale_price,
      salePrice: parsed.price,
      stock: parsed.quantity
    };
  }

  // 2⃣ Sinon, tenter la combinaison
  xml    = await fetch(`http://localhost:4000/api/combination/${encodeURIComponent(reference)}`)
                  .then(r => r.text())
                  .catch(() => null);
  parsed = parseXMLToObject(xml, 'combination');
  if (parsed && parsed.id) {
    // récupère le parent pour les prix
    const parentXml = await fetch(`http://localhost:4000/api/combination-full/${encodeURIComponent(reference)}`)
                           .then(r => r.text())
                           .catch(() => null);
    const parent = parseXMLToObject(parentXml, 'product') || { wholesale_price:0, price:0 };

    return {
      prestaId:      parsed.id,
      isCombination: true,
      purchasePrice: parent.wholesale_price,
      salePrice:     parent.price,
      stock:         parsed.quantity
    };
  }

  // 3⃣ si vraiment rien n'est trouvé
  return {
    prestaId: null,
    isCombination: false,
    purchasePrice: 0,
    salePrice: 0,
    stock: 0
  };
}


function calcHT(ttc) {
  return ttc;
}

function calcMargePourcentage(ht, achat) {
  if (!ht || ht === 0) return '❌';
  return (((ht - achat) / ht) * 100).toFixed(1) + '%';
}

async function updateStock(prestaId, quantity, currentStock, isCombination) {
  try {
    // 1. Appel à ton API proxy
    const response = await fetch('http://localhost:4000/api/update-stock', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        id: prestaId,
        quantity: quantity,
        currentStock: currentStock,
        isCombination: isCombination,
      }),
    });

    // 2. On lit toujours le texte renvoyé, qu'il y ait un 200 ou pas
    const text = await response.text();

    // 3. Si ce n’est pas OK, on affiche le statut + le message PrestaShop
    if (!response.ok) {
      alert(`❌ Erreur de mise à jour (code ${response.status}) :\n${text}`);
      console.error('Détail erreur update-stock:', response.status, text);
      return;
    }

    // 4. Succès
    alert(`✅ Stock mis à jour (ID ${prestaId})\nRéponse : ${text}`);
    console.log('Update-stock OK :', response.status, text);

  } catch (err) {
    // 5. Erreur réseau ou code JS
    alert(`❌ Exception lors de la mise à jour : ${err.message}`);
    console.error('Exception updateStock:', err);
  }
}


export default function CommandeView() {
  const [produits, setProduits] = useState([]);

  const enrichWithAPI = async (data) => {
    const enriched = await Promise.all(
      data.map(async (item) => {
        const apiData = await fetchProductOrCombination(item.reference);
        const prixHT = apiData ? calcHT(apiData.price) : null;
        const tauxMarge = apiData ? calcMargePourcentage(prixHT, apiData.wholesale_price) : '❌';

        return {
          ...item,
          prestaId: apiData?.id,
          isCombination: apiData?.isCombination ?? false,
          stock: apiData?.quantity ?? '❌',
          prixAchatPresta: apiData?.wholesale_price ?? '❌',
          prixVenteHT: prixHT ?? '❌',
          tauxMarge: tauxMarge,
        };
      })
    );
    setProduits(enriched);
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Commande Produits</h1>
      <CSVImporter onDataLoaded={enrichWithAPI} />
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-300 rounded-lg">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 text-left">Référence</th>
              <th className="px-4 py-2 text-left">Nom du produit</th>
              <th className="px-4 py-2 text-left">Marque</th>
              <th className="px-4 py-2 text-left">Prix (CSV)</th>
              <th className="px-4 py-2 text-left">Quantité</th>
              <th className="px-4 py-2 text-left">Stock</th>
              <th className="px-4 py-2 text-left">Prix achat Presta</th>
              <th className="px-4 py-2 text-left">Prix vente HT</th>
              <th className="px-4 py-2 text-left">Taux de marge</th>
              <th className="px-4 py-2 text-left">Action</th>
            </tr>
          </thead>
          <tbody>
            {produits.map((prod, idx) => (
              <tr key={idx} className="border-t">
                <td className="px-4 py-2">{prod.reference}</td>
                <td className="px-4 py-2">{prod.titre}</td>
                <td className="px-4 py-2">{prod.brand}</td>
                <td className="px-4 py-2">{prod.prix.toFixed(2)}</td>
                <td className="px-4 py-2">{prod.quantite}</td>
                <td className="px-4 py-2">{prod.stock}</td>
                <td className="px-4 py-2">{typeof prod.prixAchatPresta === 'number' ? prod.prixAchatPresta.toFixed(2) : prod.prixAchatPresta}</td>
                <td className="px-4 py-2">{typeof prod.prixVenteHT === 'number' ? prod.prixVenteHT.toFixed(2) : prod.prixVenteHT}</td>
                <td className="px-4 py-2">{prod.tauxMarge}</td>
                <td className="px-4 py-2">
                  <button
                    className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700"
                    onClick={() => updateStock(prod.prestaId, prod.quantite, prod.stock, prod.isCombination)}
                  >
                    MAJ Stock
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
