import React, { useState } from "react";
import CSVImporter from "./CSVImporter";

// Parser générique pour un <product> ou <combination> dans du XML
function parseXMLToObject(xmlString, tag) {
  if (!xmlString) return null;
  const parser = new DOMParser();
  const doc    = parser.parseFromString(xmlString, "application/xml");
  const el     = doc.querySelector(tag);
  if (!el) return null;
  const getText = t => el.querySelector(t)?.textContent.trim() || "";
  return {
    id:              getText("id"),
    wholesale_price: parseFloat(getText("wholesale_price") || "0"),
    price:           parseFloat(getText("price")           || "0"),
    quantity:        parseInt(  getText("quantity")        || "0", 10)
  };
}

// Résout un reference → { prestaId, isCombination, purchasePrice, salePrice, stock }
async function fetchProductOrCombination(reference) {
  // 1️⃣ Essayer comme produit
  let res = await fetch(`http://localhost:4000/api/product/${encodeURIComponent(reference)}`);
  let xml = await res.text();
  let p   = parseXMLToObject(xml, "product");
  if (p?.id) {
    return {
      prestaId:      p.id,
      isCombination: false,
      purchasePrice: p.wholesale_price,
      salePrice:     p.price,
      stock:         p.quantity
    };
  }

  // 2️⃣ Sinon essai combination
  res = await fetch(`http://localhost:4000/api/combination/${encodeURIComponent(reference)}`);
  xml = await res.text();
  let c = parseXMLToObject(xml, "combination");
  if (c?.id) {
    // on va chercher le parent pour les prix
    let pres = await fetch(`http://localhost:4000/api/combination-full/${encodeURIComponent(reference)}`);
    let pxml = await pres.text();
    let pr   = parseXMLToObject(pxml, "product") || { wholesale_price: 0, price: 0 };
    return {
      prestaId:      c.id,
      isCombination: true,
      purchasePrice: pr.wholesale_price,
      salePrice:     pr.price,
      stock:         c.quantity
    };
  }

  // 3️⃣ Rien trouvé → valeurs à 0
  return {
    prestaId:      null,
    isCombination: false,
    purchasePrice: 0,
    salePrice:     0,
    stock:         0
  };
}

// Calcule le taux de marge brute
function calcMarge(pvHT, paHT) {
  return pvHT > 0
    ? (((pvHT - paHT) / pvHT) * 100).toFixed(1) + "%"
    : "❌";
}

// Envoie la mise à jour stock via ton API proxy
async function updateStock(prestaId, qtyToAdd, currentStock, isCombination) {
  try {
    const resp = await fetch("http://localhost:4000/api/update-stock", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id: prestaId, quantity: qtyToAdd, currentStock, isCombination })
    });
    const text = await resp.text();
    if (!resp.ok) {
      alert(`❌ Mise à jour échouée (code ${resp.status}):\n${text}`);
    } else {
      alert(`✅ Stock mis à jour (ID ${prestaId}):\n${text}`);
    }
  } catch (e) {
    alert(`❌ Erreur réseau: ${e.message}`);
  }
}

export default function CommandeView() {
  const [produits, setProduits] = useState([]);

  const enrichWithAPI = async (rows) => {
    const data = await Promise.all(
      rows.map(async (row) => {
        // Adaptation selon ton CSVImporter : assure-toi que ces clés existent
        const reference = row["No. d'article"] || row.reference;
        const titre     = row.Titre;
        const brand     = row.Brand;
        const prixCSV   = parseFloat(row.prix) || 0;
        const quantite  = parseInt(row["qté."] || row.quantite || "0", 10);

        const api = await fetchProductOrCombination(reference);

        return {
          reference,
          titre,
          brand,
          prixCSV,
          quantite,
          prestaId:      api.prestaId,
          isCombination: api.isCombination,
          stock:         api.stock,
          prixAchat:     api.purchasePrice,
          prixVenteHT:   api.salePrice,
          tauxMarge:     calcMarge(api.salePrice, api.purchasePrice)
        };
      })
    );
    setProduits(data);
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">Commande Produits</h1>
      <CSVImporter onDataLoaded={enrichWithAPI} />
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border border-gray-300 rounded-lg text-sm">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2">Référence</th>
              <th className="px-4 py-2">Nom</th>
              <th className="px-4 py-2">Marque</th>
              <th className="px-4 py-2">Prix (CSV)</th>
              <th className="px-4 py-2">Quantité</th>
              <th className="px-4 py-2">Stock</th>
              <th className="px-4 py-2">Prix achat Presta</th>
              <th className="px-4 py-2">Prix vente HT</th>
              <th className="px-4 py-2">Taux de marge</th>
              <th className="px-4 py-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {produits.map((p, i) => (
              <tr key={i} className="border-t">
                <td className="px-4 py-2">{p.reference}</td>
                <td className="px-4 py-2">{p.titre}</td>
                <td className="px-4 py-2">{p.brand}</td>
                <td className="px-4 py-2">{p.prixCSV.toFixed(2)}</td>
                <td className="px-4 py-2">{p.quantite}</td>
                <td className="px-4 py-2">{p.stock}</td>
                <td className="px-4 py-2">{p.prixAchat.toFixed(2)}</td>
                <td className="px-4 py-2">{p.prixVenteHT.toFixed(2)}</td>
                <td className="px-4 py-2">{p.tauxMarge}</td>
                <td className="px-4 py-2">
                  <button
                    className="bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-700"
                    onClick={() =>
                      updateStock(p.prestaId, p.quantite, p.stock, p.isCombination)
                    }
                  >
                    MAJ Stock
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
